async function expandArticle(article) {
  try {
    console.log(`ç¬¬${article.id}è¨˜äº‹ã®æ›´æ–°ã‚’é–‹å§‹ã—ã¾ã™...`);
    console.log(`å¯¾è±¡: ${article.slug} (${article.title})`);
    
    // è¨˜äº‹ã‚’å–å¾—
    const post = await client.fetch(`*[_type == "post" && slug.current == "${article.slug}"][0] { _id, title, body, youtubeUrl }`);
    
    if (!post) {
      console.log('è¨˜äº‹ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“ã§ã—ãŸ');
      return { success: false, error: 'è¨˜äº‹ãŒè¦‹ã¤ã‹ã‚‰ãªã„' };
    }
    
    console.log('è¨˜äº‹ã‚¿ã‚¤ãƒˆãƒ«:', post.title);
    console.log('YouTube URL:', post.youtubeUrl ? 'ã‚ã‚Š' : 'ãªã—');
    
    // ç¾åœ¨ã®çŠ¶æ…‹ç¢ºèª
    let totalChars = 0;
    post.body.forEach(block => {
      if (block._type === 'block' && block.children) {
        const text = block.children.map(child => child.text).join('');
        totalChars += text.length;
      }
    });
    
    console.log(`ç¾åœ¨ã®æ–‡å­—æ•°: ${totalChars}æ–‡å­—`);
    console.log('ç›®æ¨™: 800-1000æ–‡å­—ã«æ‹¡å¼µ');
    
    // æ‹¡å¼µã‚³ãƒ³ãƒ†ãƒ³ãƒ„ã‚’ç”Ÿæˆ
    const expandedContent = [];
    
    article.content.forEach((item, index) => {
      if (item.intro) {
        expandedContent.push({
          _type: 'block',
          _key: 'intro',
          style: 'normal',
          children: [{ _type: 'span', text: item.intro, marks: [] }],
          markDefs: []
        });
      } else if (item.h2) {
        expandedContent.push({
          _type: 'block',
          _key: `h2-${index}`,
          style: 'h2',
          children: [{ _type: 'span', text: item.h2, marks: [] }],
          markDefs: []
        });
        expandedContent.push({
          _type: 'block',
          _key: `content-${index}`,
          style: 'normal',
          children: [{ _type: 'span', text: item.text, marks: [] }],
          markDefs: []
        });
      }
    });
    
    // æ–‡å­—æ•°ã‚«ã‚¦ãƒ³ãƒˆ
    let newTotalChars = 0;
    expandedContent.forEach(block => {
      if (block._type === 'block' && block.children) {
        const text = block.children.map(child => child.text).join('');
        newTotalChars += text.length;
      }
    });
    
    console.log(`æ–°ã—ã„æ–‡å­—æ•°: ${newTotalChars}æ–‡å­—`);
    console.log('æ–°ã—ã„æ§‹æˆ: H2è¦‹å‡ºã—6å€‹ã®çµ±ä¸€æ§‹é€ ');
    
    // è¨˜äº‹ã‚’æ…é‡ã«æ›´æ–°
    await client
      .patch(post._id)
      .set({ body: expandedContent })
      .commit();
    
    console.log(`âœ… ç¬¬${article.id}è¨˜äº‹ã®æ›´æ–°ãŒå®Œäº†ã—ã¾ã—ãŸ`);
    console.log(`ğŸ“‹ ${totalChars}æ–‡å­—â†’${newTotalChars}æ–‡å­—ã«æ‹¡å¼µ`);
    console.log('ğŸ—ï¸ H2è¦‹å‡ºã—6å€‹ã®çµ±ä¸€æ§‹é€ ã‚’é©ç”¨');
    
    // ã‚­ãƒ£ãƒƒã‚·ãƒ¥ã‚¯ãƒªã‚¢
    await client
      .patch(post._id)
      .set({ _updatedAt: new Date().toISOString() })
      .commit();
    
    console.log('ğŸ”„ ã‚­ãƒ£ãƒƒã‚·ãƒ¥ã‚¯ãƒªã‚¢å®Ÿè¡Œå®Œäº†');
    console.log('---');
    
    return { 
      success: true, 
      originalChars: totalChars, 
      newChars: newTotalChars,
      increase: newTotalChars - totalChars
    };
    
  } catch (error) {
    console.error(`âŒ ç¬¬${article.id}è¨˜äº‹ã§ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ:`, error);
    return { success: false, error: error.message };
  }
}

